(function (win, $) {

    var defaults = {
        title: '正在处理，请稍等',/*标题*/
        width: '200',/*显示宽度*/
        height: '50',/*显示高度*/
        modal: false/*是否模态*/
    };

    var dialog = {};
    /*
      显示等待图标
     */
    dialog.show = function (option) {

        option = option ? option : defaults;

        for (var item in option) {
            if (option.hasOwnProperty(item)) {
                if (item == '' || item == null) {
                    option[item] = defaults[item];
                }
            }
        }

        var html = '<div id="waittingContainer" style="position:fixed ;width: 100%; z-index: 9999; ">';
        html += '       <div id="waittingHeader" style="font-size:16px;padding: 3px;color:#fff;text-align:center;border-radius:6px;background-color:#323232;">';
        html += '           <img style="z-index:9999;vertical-align: middle;" src="data:image/gif;base64," />';
        html += '           <span>' + option.title + '</span>';
        html += '       </div>';
        html += '   </div>';

        var container = $(html);
        $(win.document.body).append(container);

        $(container).width(200);
        if (option.height != 'auto') {
            $(container).height(option.height);
        }

        $(container).css("top", $(win.document).height() / 2 - $(container).height() / 2);
        $(container).css("left", $(win.document).width() / 2 - $(container).width() / 2);
    };

    /*
      隐藏等待图标
     */
    dialog.hide = function () {
        $('#waittingContainer').remove();
    };

    win.dialog = dialog;

})(window, jQuery);